                    return;
                }
                Modernizr.on(
                    "webp",
                    function(result) {
                        var kuIfWebpClass;
                        if (result) {
                            // supported
                            kuIfWebpClass = "webp";
                        } else {
                            // not-supported
                            kuIfWebpClass = "no-webp";
                        }
                        for (var i = 0; i < kuCheckWebpElList.length; i++) {
                            kuCheckWebpElList[i].classList.add(kuIfWebpClass);
                        }
                    }
                );
            }, 500);

        });
    })();

